INSERT INTO `research_info` (`research_id`, `org_name`, `ddcr_name`, `ppe_type`, `r_title`, `r_location`, `r_priority`, `r_type`, `r_status`, `plan_sd`, `plan_ed`, `actual_sd`, `actual_ed`, `r_objective`, `r_method`, `r_abstruct`, `r_keywords`, `fund_source`, `total_budget`, `total_expenditure`, `total_refund`, `d_technology`, `p_organize`, `p_trained`, `r_programarea`, `commodity`, `outcome`, `impact`, `reference`) VALUES
(1, 'Bangladesh Rice Research Institute', 'Training Division', 'Project', 'Development and utilization of Bangladesh Rice Knowledge Bank (BRKB)', 'Gazipur : All over Bangladesh ', '', 'Applied', 'Completed', '01-01-2010', '31-12-2012', '01-01-2010', '31-12-2013', '1.Generation of updated research-based rice information and develop technology package.\r\n2.Providance of technical knowledge to enable the extension community, GO, NGOs and the private sectors.\r\n3.Web based and electronic system for quick and effective response to the farming community.\r\n', '', '', 'Bangladesh Rice Knowledge Bank, technology, farming community', 'SPGR', 6654320, 0, 0, '', '', 0, 'Development', 'Rice', '', '', ''),
(2, 'Dhaka University', 'Zoology Department ', 'Project', 'Assessment of Aquatic Pollution and Biodiversity of Some Lakes of Dhaka City ', 'Dhaka : Gulshan, Banani and Dhanmondi lake ', 'High', 'Evaluation', 'On-going', '01-03-2010', '31-12-2013', '31-03-2010', '', '1.To determine heavy metals of water, sediment and fishes.\r\n2.To know the sources and status of pollutants.\r\n3.To determine the present water and sediment quality.\r\n4.To study the effect of pollution on lake environment and aquatic organisms.\r\n5.To study the biodiversity status of lakes.\r\n6.To compare the data of polluted lakes with unpolluted lakes.\r\n', '', '', 'Aquatic Pollution, Biodiversity, Lakes, Dhaka City ', 'SPGR', 4842460, 0, 0, '', '', 0, 'Pollution', 'Environmental Science', '', '', ''),
(3, 'Bangladesh Agricultural Research Institute', 'FM &amp; PPE  Division', 'Project', 'Development and adaptation of solar pump irrigation system under eco-friendly environment ', 'Gazipur, Mymensingh, Jessore and Barisal', '', '', '', '01-01-2010', '31-12-2012', '01-06-2010', '', '1.Test different sets of solar pumps in respect of water head, pump discharge, solar energy utilization and possible area coverage for rice and non-rice crops\r\n2.Evaluate the technical feasibility of solar pump use for irrigated agriculture \r\n3.Test the most suitable solar pump in some selected areas of Gazipur, Mymensingh, Jessore and Barisal regions \r\n4.Analyze the socio-economic and environmental impacts of solar pump irrigation in the selected areas of the project \r\n', '', '', 'solar pump, irrigation system,  eco-friendly, environment ', 'SPGR', 9788000, 0, 0, '', '', 0, '', '', '', '', ''),
(4, 'Bangladesh Agricultural Research Institute', 'Oil Seed Research Center', 'Project', 'Studies on the Impact of Climate Change on Fungal Disease of Crops', 'Gazipur, Jamalpur, Ishurdi, Barisal, Hathazari and Sylhet', '', '', '', '01-04-2010', '31-12-2012', '01-04-2010', '', '1.Survey, monitoring and quantifying of fungal disease incidence of crops.\r\n2.Epidemiological study of major fungal diseases of crops for developing effective control measures.    \r\n3.Transfer of developed technologies to the farmers.\r\n', '', '', 'Impact, Climate Change, Fungal Disease of Crops, Epidemiological study, Transfer,  Technologies ', 'SPGR', 9685220, 0, 0, '', '', 0, '', '', '', '', ''),
(5, 'Bangladesh Agricultural Research Institute', 'Plant Pathology Division', 'Project', 'Development of Integrated Disease Management Technologies for Soil Borne Pathogens', 'BARI HQ, Joydebpur, RARS (Jamalpur, Jessore, Rahmatpur, Iswardi, Hathazari) and ARS Burirhat and Khagrachari', '', '', '', '01-04-2010', '31-12-2012', '01-04-2010', '', '1.To collect and identify the soil borne plant pathogens associated with major crops from different parts of the country with conventional and advance molecular techniques \r\n2.To design, implement and assess control measures of soil-borne plant pathogens involving cultural, physical, organic soil amendments and chemical approaches and their integration \r\n3.To disseminate the generated technologies to the farmers field through adaptive trials, mass demonstration, publication of leaflet, booklet and poster and arrangement of field days and workshops\r\n', '', '', 'Integrated Disease Management Technologies, Soil Borne Pathogens, Molecular Techniques, Adaptive Trials, Mass Demonstration', 'SPGR', 9430400, 0, 0, '', '', 0, '', '', '', '', ''),
(6, 'Bangladesh Rice Research Institute', 'Biotechnology Division', 'Project', 'Identification and utilization of QTLs from rice wild relatives for high yield through use of microsatellite markers', 'The entire geographic area of Bangladesh', '', '', '', '01-04-2010', '31-12-2012', '15-04-2010', '', 'Increase the yield and production of rice', '', '', 'QTLs, Microsatellite Markers', 'SPGR', 6868120, 0, 0, '', '', 0, '', '', '', '', ''),
(7, 'Bangladesh Rice Research Institute', 'Workshop Machinery and Maintenance Division', 'Project', 'Design, development, modification and introduction of self-propelled reaper and mini-power tiller to augment crop production', 'Gazipur, Rangpur and Rajshahi', '', '', '', '01-01-2010', '31-12-2012', '10-06-2010', '', '1.Enhancement of self-propelled reaper and mini-power tiller through introduction and field test at different locations in Bangladesh.\r\n2.Ensure timely harvest and cultivation to increase cropping intensity through the developed machineries.\r\n3.Minimize human drudgery and post-harvest losses.\r\n', '', '', 'self-propelled reaper, Mini-power tiller, Human drudgery', 'SPGR', 10370110, 0, 0, '', '', 0, '', '', '', '', ''),
(8, 'Bangladesh Agricultural University', 'Agronmy Department', 'Project', 'Livelihood Improvement of Farming Community in Haor Area through System Approach', 'Purba Tetulia, Mohonganj Upazila,  Netrokona ', '', '', '', '01-03-2010', '01-03-2013', '30-03-2010', '', '1.Increase productivity of field crops, vegetables, livestock and fishes in a household through the use of appropriate technologies and techniques devised/ developed/ designed/ refined by the farmers for ensuring household food security and nutritional upliftment and raising income \r\n2.Diversify enterprises, mobilize resources and intensify farming and non farming activities for in situ employment generation \r\n3.Conserve farm environment through efficient mobilization and management of natural resources for sustainable production systems \r\n4.Develop human resources for capacity building of the participants and improve their livelihood through system approach\r\n', '', '', 'Appropriate technologies, ', 'SPGR', 15481440, 0, 0, '', '', 0, '', '', '', '', ''),
