(101, 'Bangladesh Rice Research Institute', 'Agricultural Economics Division', 'Project', 'Hybrid Rice Technology and Its Sustainability at the Farm Level: A Study on Food Security for Farm Households  ', '', '', '', '', '', '', '', '', '', '', '', 'Hybrid, Rice, Technology, Sustainability, Farm Level, Food, Security, Households ', 'BARC', 0, 0, 0, '', '', 0, '', '', '', '', ''),
(102, 'Bangladesh Jute Research Institute', 'Jute farming Systems Division', 'Project', 'Improvement of jute based cropping pattern and jute seed production techniques in southern areas of Bangladesh', 'Pakhimara sub - station, BJRI,Amtali (Barguna), Dumki (Patuakhali)', '', '', 'New', '01-07-2011', '30-06-2014', '', '', '1.To identify suitable variety of jute (Capsularies) and Kenaf for seed production at farm level in the southern area.\r\n2.To develop jute based cropping pattern for potential use of land of the area. \r\n3.To identify jute and jute seed production constraints in the area.\r\n4.To find out the appropriate technique for jute seed production.  \r\n', '', '', 'cropping pattern, seed, southern, potential, technique,  Capsularies, Kenaf ', 'BARC', 1000000, 0, 0, '', '', 0, '', '', '', '', ''),
(103, 'Bangladesh Institute of Nuclear Agriculture', 'Training, Communication and Publication Division', 'Project', 'On Farm Validation of Pulse Variety Binamoog-8 and Binamasur-5 in Natore, Bogra and Bhola Districts \r\n', '', '', '', 'New', '01-07-2011', '30-06-2013', '', '', '1.To ascertain achievable yield of Binamoog-8 and Binamasur-6 at farmers fields; and\r\n2.To assess farmers perception of Binamoog-8 and Binamasur-6 of the following characteristics :\r\n  Earliness,Uniform maturity,Non shattering of pods,Tolerant to major insect-pest,Bold seeded,Shiny seed  coat,Adaptability to fit in intensive cropping pattern \r\n', '', '', 'Farm Validation,  Pulse Variety Binamoog-8,  Binamasur-5, perception ', 'BARC', 375000, 0, 0, '', '', 0, '', '', '', '', ''),
