(909, 'Bangladesh Agricultural Research Institute', 'Crops', 'Project', 'Expanded farming system research and development programme for technology dissemination in greater extrapolation areas of FSRD site at Goyeshpur, Pabna.', '', '', 'Demonstrative', 'Completed', '01-07-2000', '31-12-2000', '', '', '1. To disseminate technologies in extrapolation areas for increasing output and income.\r\n2. To promote integrated approach to increase farm productivity.\r\n3. To improve farm skill of the extension providers as well as farmers.\r\n4. To strengthen linkage among researchers, extension providers and farmers to expedite technology transfer process.\r\n5. To develop agri-business and enterprenuership in farming.', '', '', '', 'ARMP', 2377000, 0, 0, '', '', 0, 'FSRD', '', '', '', ''),
(908, 'Bangabandhu Sheikh Mujibur Rahman Agricultural University', 'Crops', 'Project', 'Validation of Vegetable Production Technologies of IPSA.', '', '', 'Adaptive', 'Completed', '01-04-1998', '31-03-2001', '01-05-1998', '', '(1) Conduct regional trial in different environment to examine productivity, diseases resistant and overall performance for adoptive evaluation of varieties, rapid release and extension.\r\n(2) Promote vegetable seed multiplication locally\r\n(3) Improvement of homestead gardening and its utilization.\r\n(4) Development of management technologies/cropping systems/crop rotation with vegetables.', '', '', '', 'ARMP', 1000000, 0, 0, '', '', 0, 'Horticulture', 'Vegetable', '', '', ''),
